function generateChartAnnotationLabels(annotationLabels: AnnotationLabel[], minXScale?: string): ChartOptions {
  let annotationLabelConfigurations = annotationLabels.map((annotationLabel) => {
    return {
      type: 'line',
      scaleID: 'x',
      value: annotationLabel.annotationValue,
      borderColor: 'orange',
      borderWidth: 2,
      label: {
        position: 'end',
        enabled: true,
        color: 'orange',
        content: annotationLabel.annotationContent,
        font: {
          weight: 'bold'
        }
      }
    }
  })

  let chartOptionsToReturn: ChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    elements: {
      line: {
        tension: 0.5
      },
    },
    plugins: {
      legend: { display: true },
      annotation: {
        annotations: annotationLabelConfigurations as any, // Added as any as the linter has some trouble with this object
      }
    }
  }

  if (minXScale) {
    chartOptionsToReturn.scales = {
      x: {
          min: minXScale,
      }
    }
  }

  return chartOptionsToReturn;
}

